@article{zangIntergenerationalUpwardMobility2021,
  title = {Intergenerational Upward Mobility and Racial Differences in Mortality among Young Adults: {{Evidence}} from County-Level Analyses},
  shorttitle = {Intergenerational Upward Mobility and Racial Differences in Mortality among Young Adults},
  author = {Zang, Emma and Kim, Nathan},
  year = {2021},
  month = jul,
  journal = {Health \& Place},
  volume = {70},
  pages = {102628},
  issn = {1353-8292},
  doi = {10.1016/j.healthplace.2021.102628},
  abstract = {Inspired by the influential ``deaths of despair'' narrative, which emphasizes the role of worsening economic opportunity in driving the increasing mortality for non-Hispanic Whites in the recent decades, a rising number of studies have provided suggestive evidence that upward mobility levels across counties may partly explain variations in mortality rates. A gap in the literature is the lack of life-course studies examining the relationship between early-life upward mobility and later-life mortality across counties. Another gap is the lack of studies on how the relationship between upward mobility and mortality across counties varies across diverse sociodemographic populations. This study examines differences across race and sex in the relationship between early-life intergenerational upward mobility and early adulthood mortality at the county level. We use administrative data on upward mobility and vital statistics data on mortality across 3030 counties for those born between 1978 and 1983. We control for a variety of county-level socioeconomic variables in a model with fixed effects for state and year. Subgroup analyses by educational attainment and urban status were also performed for each race-sex combination. Results show strong negative relationships between early-life upward mobility and early adulthood mortality across racial-sex combinations, with a particularly greater magnitude for non-Hispanic Black males. In addition, individuals without a college degree and living in urban counties are particularly affected by early life upward mobility. The findings of this study highlight the vulnerability of less-educated, young urban Black males, due to the intersecting effects of the urban context, education, race, and sex.},
  langid = {english},
  keywords = {County-level analysis,Early adulthood mortality,Intergenerational mobility,Racial disparities,Sex disparities},
  file = {/home/nathan/Zotero/storage/BS86L85T/S1353829221001246.html}
}

@article{zangRegionalDifferencesPhysical2021,
  title = {U.{{S}}. Regional Differences in Physical Distancing: {{Evaluating}} Racial and Socioeconomic Divides during the {{COVID-19}} Pandemic},
  shorttitle = {U.{{S}}. Regional Differences in Physical Distancing},
  author = {Zang, Emma and West, Jessica and Kim, Nathan and Pao, Christina},
  year = {2021},
  month = nov,
  journal = {PLOS ONE},
  volume = {16},
  number = {11},
  pages = {e0259665},
  publisher = {{Public Library of Science}},
  issn = {1932-6203},
  doi = {10.1371/journal.pone.0259665},
  abstract = {Health varies by U.S. region of residence. Despite regional heterogeneity in the outbreak of COVID-19, regional differences in physical distancing behaviors over time are relatively unknown. This study examines regional variation in physical distancing trends during the COVID-19 pandemic and investigates variation by race and socioeconomic status (SES) within regions. Data from the 2015\textendash 2019 five-year American Community Survey were matched with anonymized location pings data from over 20 million mobile devices (SafeGraph, Inc.) at the Census block group level. We visually present trends in the stay-at-home proportion by Census region, race, and SES throughout 2020 and conduct regression analyses to examine these patterns. From March to December, the stay-at-home proportion was highest in the Northeast (0.25 in March to 0.35 in December) and lowest in the South (0.24 to 0.30). Across all regions, the stay-at-home proportion was higher in block groups with a higher percentage of Blacks, as Blacks disproportionately live in urban areas where stay-at-home rates were higher (0.009 [CI: 0.008, 0.009]). In the South, West, and Midwest, higher-SES block groups stayed home at the lowest rates pre-pandemic; however, this trend reversed throughout March before converging in the months following. In the Northeast, lower-SES block groups stayed home at comparable rates to higher-SES block groups during the height of the pandemic but diverged in the months following. Differences in physical distancing behaviors exist across U.S. regions, with a pronounced Southern and rural disadvantage. Results can be used to guide reopening and COVID-19 mitigation plans.},
  langid = {english},
  keywords = {Cell phones,COVID 19,Human mobility,Medical risk factors,Pandemics,Population density,Public and occupational health,Socioeconomic aspects of health},
  file = {/home/nathan/Zotero/storage/W2BHG483/Zang et al. - 2021 - U.S. regional differences in physical distancing .pdf;/home/nathan/Zotero/storage/9DLD3QS5/article.html}
}

